import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import {
  createProject,
  deleteProject,
  listProjects,
  updateProject,
} from "../actions/projectActions";
import { PROJECT_CREATE_RESET } from "../constants/projectConstants";

const AdminProjectsListScreen = ({ history }) => {
  const dispatch = useDispatch();

  const projectList = useSelector((state) => state.projectList);
  const { loading, error, projects } = projectList;

  const projectDelete = useSelector((state) => state.projectDelete);
  const {
    loading: deleteLoading,
    error: deleteError,
    success: deleteSuccess,
  } = projectDelete;

  const projectCreate = useSelector((state) => state.projectCreate);
  const {
    loading: createLoading,
    error: createError,
    success: createSuccess,
    project: createProject,
  } = projectCreate;

  const adminLogin = useSelector((state) => state.adminLogin);
  const { userInfo } = adminLogin;

  useEffect(() => {
    dispatch({ type: PROJECT_CREATE_RESET });
    if (!userInfo.isAdmin) {
      history.push("/");
    }

    if (createSuccess) {
      history.push(`/viewprojects/project/${createProject._id}/edit`);
    } else {
      dispatch(listProjects());
    }
  }, [dispatch, userInfo, createSuccess, history, createProject]);

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-8 offset-md-2 my-4">
          <h4>Project List</h4>
          <table className="table table-sm table-dark">
            <thead>
              <tr>
                <th scope="col">ID</th>
                <th scope="col">Title</th>
                <th scope="col">Finished</th>
                <th scope="col">Status</th>
                <th scope="col"></th>
              </tr>
            </thead>
            <tbody>
              {projects.map((project) => (
                <tr>
                  <th>{project._id}</th>
                  <td>{project.title}</td>
                  <td>Finished</td>
                  <td>Live</td>
                  <td>
                    <button className="btn b"></button>
                  </td>
                </tr>
              ))}
              <tr>
                <th>2</th>
                <td>Jacob</td>
                <td>Thornton</td>
                <td>@fat</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminProjectsListScreen;
