import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useDispatch, useSelector } from "react-redux";
import { listProjects } from "../actions/projectActions";

const ProjectsListScreen = () => {
  const dispatch = useDispatch();
  const [badgeColor, setBadgeColor] = useState("primary");

  const projectList = useSelector((state) => state.projectList);
  const { loading, error, projects } = projectList;

  useEffect(() => {
    dispatch(listProjects());
    projects.map((x) => {
      if (x.status === "Live") {
         return setBadgeColor("success");
      } else if (x.status === "Not Live") {
        return setBadgeColor("secondary")
      } else if (x.status === "Under Construction") {
        return set
      }
    });
  }, [dispatch]);

  return (
    <>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div className="container pt-3">
          <div className="row">
            <div className="col-12">
              <h2>Projects</h2>

              <hr style={{ backgroundColor: "white" }} />
            </div>
          </div>
          {projects.map((project) => (
            <div className="row py-3" key={project._id}>
              <div className="col-md-7">
                <Link to={`/project/${project._id}`}>
                  <img
                    className="img-fluid rounded mb-3 mb-md-0"
                    src="http://placehold.it/700x300"
                    alt=""
                  />
                </Link>
              </div>
              <div className="col-md-5">
                <h3>{project.title}</h3>
                <span
                  className={`badge badge-pill badge-${
                    project.status === "Live" ? "success" : "primary"
                  }`}
                >
                  {project.status}
                </span>
                <p>{project.shortDescription}</p>
                <div className="row">
                  <div className="col-md-6">
                    <h4>Frontend</h4>
                    <p>{project.frontendStack.join(" | ")}</p>
                  </div>
                  <div className="col-md-6">
                    <h4>Backend</h4>
                    <p>{project.backendStack.join(" | ")}</p>
                  </div>
                </div>
                <Link
                  className="btn btn-primary"
                  to={`/project/${project._id}`}
                >
                  View Project
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default ProjectsListScreen;
