import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import {
  createNewProject,
  deleteProject,
  listProjects,
  updateProject,
} from "../actions/projectActions";
import { PROJECT_CREATE_RESET } from "../constants/projectConstants";

const AdminProjectsListScreen = ({ history }) => {
  const dispatch = useDispatch();

  const projectList = useSelector((state) => state.projectList);
  const { loading, error, projects } = projectList;

  const projectDelete = useSelector((state) => state.projectDelete);
  const {
    loading: deleteLoading,
    error: deleteError,
    success: deleteSuccess,
  } = projectDelete;

  const projectCreate = useSelector((state) => state.projectCreate);
  const {
    loading: loadingCreate,
    error: errorCreate,
    success: successCreate,
    project: createdProject,
  } = projectCreate;

  const adminLogin = useSelector((state) => state.adminLogin);
  const { userInfo } = adminLogin;

  useEffect(() => {
    dispatch({ type: PROJECT_CREATE_RESET });
    if (!userInfo.isAdmin) {
      history.push("/admin/login");
    }

    if (successCreate) {
      history.push(`/admin/project/${createdProject._id}/edit`);
    } else {
      dispatch(listProjects());
    }
  }, [dispatch, userInfo, successCreate, history, createdProject]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure you delete this project?")) {
      dispatch(deleteProject(id));
    }
  };

  const createProjectHandler = (e) => {
    dispatch(createNewProject());
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-8 offset-md-2 my-4">
          <div className="col">
            <h4>Project List</h4>
          </div>
          <div className="col text-right mb-1">
            <button className="btn btn-primary" onClick={createProjectHandler}>
              Add Project
            </button>
          </div>
          {deleteLoading && <Loader />}
          {deleteError && <Message variant="danger">{deleteError}</Message>}
          {loadingCreate && <Loader />}
          {errorCreate && <Message variant="danger">{errorCreate}</Message>}

        </div>
      </div>
    </div>
  );
};

export default AdminProjectsListScreen;
