import { useEffect } from "react";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useDispatch, useSelector } from "react-redux";
import { listProjects } from "../actions/projectActions";

const ProjectsListScreen = () => {
  const dispatch = useDispatch();

  const projectList = useSelector((state) => state.projectList);
  const { loading, error, projects } = projectList;

  useEffect(() => {
    dispatch(listProjects);
  }, [dispatch]);

  return (
    <div className="container pt-3">
      <div className="row">
        <div className="col-12">
          <h2>Projects</h2>
          <hr style={{ backgroundColor: "white" }} />
        </div>
      </div>
      <div class="row">
        {loading && <Loader />}
        {error && <Message variant="danger">{error}</Message>}
        {projects.map((project) => (
          <>
            <div class="col-md-7">
              <Link to="/">
                <img
                  class="img-fluid rounded mb-3 mb-md-0"
                  src="http://placehold.it/700x300"
                  alt=""
                />
              </Link>
            </div>
            <div class="col-md-5">
              <h3>Project One</h3>
              <p>
                Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                Laudantium veniam exercitationem expedita laborum at voluptate.
                Labore, voluptates totam at aut nemo deserunt rem magni pariatur
                quos perspiciatis atque eveniet unde.
              </p>
              <Link class="btn btn-primary" to={`/`}>
                View Project
              </Link>
            </div>
          </>
        ))}
      </div>
    </div>
  );
};

export default ProjectsListScreen;
