import { useEffect } from "react";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useDispatch, useSelector } from "react-redux";
import { listProjectDetails } from "../actions/projectActions";

const ProjectDetailsScreen = ({ match }) => {
  const dispatch = useDispatch();

  const projectDetails = useSelector((state) => state.projectDetails);
  const { loading, error, project } = projectDetails;

  console.log(project);

  useEffect(() => {
    window.scrollTo(0, 0);
    dispatch(listProjectDetails(match.params.id));
  }, [dispatch, match]);

  return (
    <>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div className="container">
          <h1 className="my-4">{project.title}</h1>
          <div className="row pb-4">
            <div className="col-md-7">
              <img
                className="img-fluid"
                src="http://placehold.it/750x500"
                alt=""
              />
            </div>
            <div className="col-md-5">
              <h3 className="my-3">Project Description</h3>
              <p>{project.longDescription}</p>
              <div className="row">
                <div className="col-md-4 text-center">
                  <h6>Frontend Stack</h6>
                  <ul className="text-left">
                    {project.frontendStack.map((x, index) => (
                      <li key={x.index}>{x.toString()}</li>
                    ))}
                  </ul>
                </div>
                <div className="col-md-4 text-center">
                  <h6>Backend Stack</h6>
                  <ul className="text-left">
                    {project.backendStack.map((x, index) => (
                      <li key={x.index}>{x.toString()}</li>
                    ))}
                  </ul>
                </div>
                <div className="col-md-4 text-center">
                  <h6>Database Stack</h6>
                  <ul className="text-left">
                    {project.databaseStack.map((x, index) => (
                      <li key={x.index}>{x.toString()}</li>
                    ))}
                  </ul>
                </div>
              </div>
              <h3 className="my-3">Project Features</h3>

              <div className="btn-group my-4">
                <button className="btn btn-primary details">Trello</button>
                <button className="btn btn-secondary">GitHub</button>
                <button className="btn btn-success">Project Website</button>
              </div>
              <Link className="btn btn-link" to="/projects">
                Go Back
              </Link>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ProjectDetailsScreen;
