import { useEffect } from "react";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useDispatch, useSelector } from "react-redux";
import { listProjectDetails } from "../actions/projectActions";

const ProjectDetailsScreen = ({ match }) => {
  const dispatch = useDispatch();

  const projectDetails = useSelector((state) => state.projectDetails);
  const { loading, error, project } = projectDetails;

  useEffect(() => {
    dispatch(listProjectDetails(match.params.id));
  }, [dispatch, match]);

  return (
    <div className="container">
      <Link className="btn btn-secondary my-2" to="/projects">
        Go Back
      </Link>
      <h1 className="my-4">Project Header</h1>
      <div className="row pb-4">
        <div className="col-md-8">
          <img className="img-fluid" src="http://placehold.it/750x500" alt="" />
        </div>
        <div className="cl-md-4">
          <h3 className="my-3">Project Description</h3>
          <p>This is the description</p>
          <h3 className="my-3">Project Details</h3>
          <ul>
            <li>Stuff</li>
            <li>Stuff</li>
            <li>Stuff</li>
            <li>Stuff</li>
          </ul>
          <div className="btn-group">
            <button className="btn btn-primary">Trello</button>
            <button className="btn btn-secondary">GitHub</button>
            <button className="btn btn-success">Project Website</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetailsScreen;
