import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useSelector, useDispatch } from "react-redux";
import { getContactDetails } from "../actions/contactActions";

const AdminContactDetailsScreen = ({ match }) => {
  const [haveRead, setHaveRead] = useState(false);

  const dispatch = useDispatch();

  const contactDetails = useSelector((state) => state.contactDetails);
  const { loading, error, contact } = contactDetails;
  console.log(contact);

  useEffect(() => {
    dispatch(getContactDetails(match.params.id));
  }, [dispatch, match]);

  // haveread handler
  const submitHandler = (e) => {
    e.preventDefault()
  }

  return (
    <>
      <Link className="btn btn-light m-3" to="/admin/viewcontacts">
        Go Back
      </Link>
      <div className="container">
        <div className="row">
          <div className="col-md-8 offset-md-2 my-4">
            <h2>Contact Details</h2>
            {loading ? (
              <Loader />
            ) : error ? (
              <Message>{error}</Message>
            ) : (
              <>
                <hr />
                <p>
                  <em>Name: </em>
                  {contact.name}
                </p>
                <p>
                  <em>Phone:</em>
                  <br />
                  {contact.phone}
                </p>
                <p>
                  <em>Email: </em>
                  {contact.email}
                </p>
                <p>
                  <em>Message:</em>
                  <br />
                  {contact.message}
                </p>
                <hr />
                <form>
                  <div className="form-group">
                    <label htmlFor="havereadSelection">
                      Confirm Read Message?
                    </label>
                    <div className="form-check">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        value={haveRead}
                        id="defaultCheck1"
                        onChange={(e) => setHaveRead(e.target.value)}
                      />
                      <label
                        className="form-check-label mb-3"
                        htmlFor="defaultCheck1"
                      >
                        Yes, I have read the contact information.
                      </label>
                    </div>
                    <button type="submit" className="btn btn-primary">
                      Update
                    </button>
                  </div>
                </form>
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default AdminContactDetailsScreen;
